import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Button, Form, FormGroup, Label, Input, Container } from "reactstrap";
import { addItem } from "../utils/Api"; // Assuming this function handles adding items

function AddItemPage() {
    const [formData, setFormData] = useState({
        type: "snack",
        name: "",
        description: "",
        recipe: "",
        serve: ""
    });

    const history = useHistory();

    function handleChange(evt) {
        const { name, value } = evt.target;
        setFormData(formData => ({
            ...formData,
            [name]: value
        }));
    }

    async function handleSubmit(evt) {
        evt.preventDefault();
        try {
            await addItem(formData.type, {
                name: formData.name,
                description: formData.description,
                recipe: formData.recipe,
                serve: formData.serve
            });
            history.push(`/${formData.type}s`); // Redirect to snacks or drinks listing
        } catch (err) {
            console.error("Error adding item", err);
        }
    }

    return (
        <Container className="my-4">
            <h2>Add a New Item</h2>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <Label for="type">Type</Label>
                    <Input
                        type="select"
                        name="type"
                        id="type"
                        value={formData.type}
                        onChange={handleChange}
                    >
                        <option value="snack">Snack</option>
                        <option value="drink">Drink</option>
                    </Input>
                </FormGroup>
                <FormGroup>
                    <Label for="name">Name</Label>
                    <Input
                        type="text"
                        name="name"
                        id="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="description">Description</Label>
                    <Input
                        type="textarea"
                        name="description"
                        id="description"
                        value={formData.description}
                        onChange={handleChange}
                        required
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="recipe">Recipe</Label>
                    <Input
                        type="textarea"
                        name="recipe"
                        id="recipe"
                        value={formData.recipe}
                        onChange={handleChange}
                    />
                </FormGroup>
                <FormGroup>
                    <Label for="serve">Serve</Label>
                    <Input
                        type="text"
                        name="serve"
                        id="serve"
                        value={formData.serve}
                        onChange={handleChange}
                    />
                </FormGroup>
                <Button type="submit" color="primary">Add {formData.type}</Button>
            </Form>
        </Container>
    );
}

export default AddItemPage;
