import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, CardBody, CardTitle, CardText } from "reactstrap";
import { getItems } from "../utils/Api"; // Assume a helper to fetch items from an API

function HomePage() {
  const [snackCount, setSnackCount] = useState(0);
  const [drinkCount, setDrinkCount] = useState(0);

  useEffect(() => {
    async function fetchData() {
      // Fetch snacks and drinks
      const snacks = await getItems("snacks");
      const drinks = await getItems("drinks");

      // Set the counts
      setSnackCount(snacks.length);
      setDrinkCount(drinks.length);
    }

    fetchData();
  }, []);

  return (
    <Container>
      <Row className="justify-content-center my-4">
        <Col md={8}>
          <Card>
            <CardBody className="text-center">
              <CardTitle>
                <h3 className="font-weight-bold">
                  Welcome to Snack or Booze!
                </h3>
              </CardTitle>
              <CardText>
                We have a delicious selection of snacks and drinks to satisfy your cravings!
              </CardText>
              <Row>
                <Col>
                  <CardText>
                    <b>{snackCount}</b> Snacks Available
                  </CardText>
                </Col>
                <Col>
                  <CardText>
                    <b>{drinkCount}</b> Drinks Available
                  </CardText>
                </Col>
              </Row>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default HomePage;
